function formatLoanVolume(volume: number): string {
  if (volume >= 1) {
    return `$${volume.toFixed(1)}M`;
  }
  return `$${(volume * 1000).toFixed(0)}K`;
}

interface USMapProps {
  onStateClick?: (state: StateData) => void;
}

export default function USMap({ onStateClick }: USMapProps) {
  const [hoveredState, setHoveredState] = useState<StateData | null>(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  const handleMouseEnter = (e: React.MouseEvent, stateAbbr: string) => {
    const stateData = statesData.find(s => s.abbreviation === stateAbbr);
    if (stateData) {
      setHoveredState(stateData);
      const rect = e.currentTarget.getBoundingClientRect();
      const svgRect = (e.currentTarget.closest('svg') as SVGElement)?.getBoundingClientRect();
      if (svgRect) {
        setTooltipPosition({
          x: rect.left - svgRect.left + rect.width / 2,
          y: rect.top - svgRect.top - 10,
        });
      }
    }
  };

  const handleMouseLeave = () => {
    setHoveredState(null);
  };

  const handleClick = (stateAbbr: string) => {
    const stateData = statesData.find(s => s.abbreviation === stateAbbr);
    if (stateData && stateData.isEligible && onStateClick) {
      onStateClick(stateData);
    }
  };

  return (
    <div className="relative w-full" data-testid="us-map-container">
      <svg
        viewBox="0 0 960 600"
        className="w-full h-auto"
        style={{ maxHeight: '500px' }}
      >
        {Object.entries(statePaths).map(([abbr, path]) => {
          const stateData = statesData.find(s => s.abbreviation === abbr);
          const isEligible = stateData?.isEligible ?? false;
          const isHovered = hoveredState?.abbreviation === abbr;
          
          return (
            <path
              key={abbr}
              d={path}
              className={`
                transition-all duration-200 cursor-pointer stroke-white stroke-[0.5]
                ${isEligible 
                  ? isHovered 
                    ? 'fill-primary' 
                    : 'fill-primary/70 hover:fill-primary'
                  : 'fill-muted cursor-not-allowed'
                }
              `}
              onMouseEnter={(e) => handleMouseEnter(e, abbr)}
              onMouseLeave={handleMouseLeave}
              onClick={() => handleClick(abbr)}
              data-testid={`state-${abbr.toLowerCase()}`}
            />
          );
        })}
      </svg>
      
      {hoveredState && (
        <div
          className="absolute z-50 pointer-events-none transform -translate-x-1/2 -translate-y-full"
          style={{
            left: tooltipPosition.x,
            top: tooltipPosition.y,
          }}
        >
          <div className="bg-card border rounded-lg shadow-lg p-3 min-w-[180px]">
            <p className="font-bold text-foreground text-sm mb-1" data-testid="tooltip-state-name">
              {hoveredState.name}
            </p>
            {hoveredState.isEligible ? (
              <>
                <div className="flex justify-between text-xs text-muted-foreground">
                  <span>Loans Closed:</span>
                  <span className="font-semibold text-foreground" data-testid="tooltip-loans-closed">
                    {hoveredState.loansClosed.toLocaleString()}
                  </span>
                </div>
                <div className="flex justify-between text-xs text-muted-foreground">
                  <span>Total Volume:</span>
                  <span className="font-semibold text-foreground" data-testid="tooltip-loan-volume">
                    {formatLoanVolume(hoveredState.loanVolume)}
                  </span>
                </div>
                <p className="text-xs text-primary mt-2 font-medium">Click to view details</p>
              </>
            ) : (
              <p className="text-xs text-muted-foreground">Not currently lending in this state</p>
            )}
          </div>
          <div className="w-3 h-3 bg-card border-r border-b transform rotate-45 absolute left-1/2 -translate-x-1/2 -bottom-1.5" />
        </div>
      )}
    </div>
  );
}
